CREATE TABLE `show_lang` (
  `show_id` int(10) unsigned NOT NULL COMMENT 'ID шоу',
  `show_lang` char(2) NOT NULL COMMENT 'Язык',
  `show_title` varchar(65) DEFAULT NULL COMMENT 'Название',
