  PRIMARY KEY (`show_id`,`show_lang`),
  KEY `fk_movie_lang_idx` (`show_lang`),
  CONSTRAINT `fk_show_id_from_lang` FOREIGN KEY (`show_id`) REFERENCES `show` (`show_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_show_lang_from_lang` FOREIGN KEY (`show_lang`) REFERENCES `langs` (`lang`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Информация о шоу на доступных языках';
